      ],
      value1: "",
      value2: "",
      isShow: false,
      isShow1: false,
      isShow2: false,
      isShow4: false,
      isShow5: false,
      inpVal: "",
      inpVal1: "",
      inpVal2: "",
      inpVal4: "",
      inpVal5: ""
    };
  },
  watch: {
    input10(e) {
      if (e) {
        this.isShow = false;
      }
    },
    value(e) {
      if (e) {
        this.isShow1 = false;
      }
    },
    values(e) {
      if (e) {
        this.isShow2 = false;
      }
    },
    value1(e) {
      if (e) {
        this.isShow4 = false;
      }
    },
    value2(e) {
      if (e) {
        this.isShow5 = false;
      }
    }
  },
  methods: {
    getValue(e) {
      this.inpVal = e.target.value;
    },
    getValue1(e) {
      this.inpVal1 = e.target.value;
    },
    getValue2(e) {
      this.inpVal2 = e.target.value;
    },
    getValue4(e) {
      this.inpVal4 = e.target.value;
    },
    getValue5(e) {
      this.inpVal5 = e.target.value;
    },
    handleBtn(e) {
      if (
        this.inpVal == "" &&  
        this.inpVal1 == "" && 
        this.inpVal2 == "" && 
        this.inpVal4 == "" && 
        this.inpVal5 == ""
      ) {
        this.isShow = true;
        this.isShow1 = true;
        this.isShow2 = true;
        this.isShow4 = true;
        this.isShow5 = true;
      } else {
        this.isShow = false;
        this.isShow1 = false;
        this.isShow2 = false;
        this.isShow4 = false;
        this.isShow5 = false;
      }
    }
  }
};
